import ValTown from '@valtown/sdk';

const client = new ValTown();

const user = await client.alias.username.retrieve('username');

console.log(user.id);

import ValTown from '@valtown/sdk';

const client = new ValTown();

const profile = await client.me.profile.retrieve();

console.log(profile.id);

import ValTown from '@valtown/sdk';

const client = new ValTown();

const blobs = await client.blobs.list();

console.log(blobs);

import ValTown from '@valtown/sdk';

const client = new ValTown();

const blob = await client.blobs.get('x');

console.log(blob);

const content = await blob.blob();
console.log(content);

import ValTown from '@valtown/sdk';

const client = new ValTown({
  bearerToken: process.env['VAL_TOWN_API_KEY'], // This is the default and can be omitted
});

await client.blobs.store('x');

import ValTown from '@valtown/sdk';

const client = new ValTown({
  bearerToken: process.env['VAL_TOWN_API_KEY'], // This is the default and can be omitted
});

await client.blobs.delete('x');

import ValTown from '@valtown/sdk';

const client = new ValTown();

const user = await client.users.retrieve('182bd5e5-6e1a-4fe4-a799-aa6d9a6ab26e');

console.log(user.id);

import ValTown from '@valtown/sdk';

const client = new ValTown({
  bearerToken: process.env['VAL_TOWN_API_KEY'], // This is the default and can be omitted
});

const resultSet = await client.sqlite.execute({ statement: 'SELECT 1;' });

console.log(resultSet.lastInsertRowid);

import ValTown from '@valtown/sdk';

const client = new ValTown({
  bearerToken: process.env['VAL_TOWN_API_KEY'], // This is the default and can be omitted
});

const resultSets = await client.sqlite.batch({ statements: ['SELECT 1;'], mode: 'read' });

console.log(resultSets);

import ValTown from '@valtown/sdk';

const client = new ValTown({
  bearerToken: process.env['VAL_TOWN_API_KEY'], // This is the default and can be omitted
});

const response = await client.emails.send({
  html: 'Hello <strong>world</strong>',
  subject: 'An important message',
  text: 'Hello world',
});

console.log(response.message);

import ValTown from '@valtown/sdk';

const client = new ValTown();

const traces = await client.telemetry.traces.list({ direction: 'asc', limit: 1 });

console.log(traces.data);

import ValTown from '@valtown/sdk';

const client = new ValTown();

const logs = await client.telemetry.logs.list({ direction: 'asc', limit: 1 });

console.log(logs.data);

import ValTown from '@valtown/sdk';

const client = new ValTown();

const val = await client.vals.retrieve('182bd5e5-6e1a-4fe4-a799-aa6d9a6ab26e');

console.log(val.id);

import ValTown from '@valtown/sdk';

const client = new ValTown();

await client.vals.delete('182bd5e5-6e1a-4fe4-a799-aa6d9a6ab26e');

import ValTown from '@valtown/sdk';

const client = new ValTown({
  bearerToken: process.env['VAL_TOWN_API_KEY'], // This is the default and can be omitted
});

// Automatically fetches more pages as needed.
for await (const val of client.vals.list({ limit: 1 })) {
  console.log(val.id);
}

import ValTown from '@valtown/sdk';

const client = new ValTown();

const val = await client.vals.create({ name: 'myVal', privacy: 'public', description: 'My val' });

console.log(val.id);

import ValTown from '@valtown/sdk';

const client = new ValTown();

const branch = await client.vals.branches.retrieve('182bd5e5-6e1a-4fe4-a799-aa6d9a6ab26e', {
  val_id: '182bd5e5-6e1a-4fe4-a799-aa6d9a6ab26e',
});

console.log(branch.id);

import ValTown from '@valtown/sdk';

const client = new ValTown();

await client.vals.branches.delete('182bd5e5-6e1a-4fe4-a799-aa6d9a6ab26e', {
  val_id: '182bd5e5-6e1a-4fe4-a799-aa6d9a6ab26e',
});

import ValTown from '@valtown/sdk';

const client = new ValTown();

// Automatically fetches more pages as needed.
for await (const branchListResponse of client.vals.branches.list('182bd5e5-6e1a-4fe4-a799-aa6d9a6ab26e', {
  limit: 1,
  offset: 0,
})) {
  console.log(branchListResponse.id);
}

import ValTown from '@valtown/sdk';

const client = new ValTown();

const branch = await client.vals.branches.create('182bd5e5-6e1a-4fe4-a799-aa6d9a6ab26e', {
  name: 'my-branch',
  branchId: '00000000-0000-0000-0000-000000000000',
});

console.log(branch.id);

import ValTown from '@valtown/sdk';

const client = new ValTown();

// Automatically fetches more pages as needed.
for await (const fileRetrieveResponse of client.vals.files.retrieve('182bd5e5-6e1a-4fe4-a799-aa6d9a6ab26e', {
  limit: 1,
  offset: 0,
  path: 'path',
  recursive: true,
})) {
  console.log(fileRetrieveResponse.id);
}

import ValTown from '@valtown/sdk';

const client = new ValTown();

const file = await client.vals.files.create('182bd5e5-6e1a-4fe4-a799-aa6d9a6ab26e', {
  path: 'path',
  type: 'directory',
});

console.log(file.id);

import ValTown from '@valtown/sdk';

const client = new ValTown();

await client.vals.files.delete('182bd5e5-6e1a-4fe4-a799-aa6d9a6ab26e', { path: 'path', recursive: true });

import ValTown from '@valtown/sdk';

const client = new ValTown();

const file = await client.vals.files.update('182bd5e5-6e1a-4fe4-a799-aa6d9a6ab26e', { path: 'path' });

console.log(file.id);

import ValTown from '@valtown/sdk';

const client = new ValTown();

// Automatically fetches more pages as needed.
for await (const environmentVariableListResponse of client.vals.environmentVariables.list(
  '182bd5e5-6e1a-4fe4-a799-aa6d9a6ab26e',
  { limit: 1, offset: 0 },
)) {
  console.log(environmentVariableListResponse.createdAt);
}

import ValTown from '@valtown/sdk';

const client = new ValTown();

const environmentVariable = await client.vals.environmentVariables.create(
  '182bd5e5-6e1a-4fe4-a799-aa6d9a6ab26e',
  { key: 'key', value: 'value' },
);

console.log(environmentVariable.createdAt);

import ValTown from '@valtown/sdk';

const client = new ValTown();

const environmentVariable = await client.vals.environmentVariables.update('key', {
  val_id: '182bd5e5-6e1a-4fe4-a799-aa6d9a6ab26e',
  value: 'value',
});

console.log(environmentVariable.createdAt);

import ValTown from '@valtown/sdk';

const client = new ValTown();

await client.vals.environmentVariables.delete('key', { val_id: '182bd5e5-6e1a-4fe4-a799-aa6d9a6ab26e' });

import ValTown from '@valtown/sdk';

const client = new ValTown();

const response = await client.vals.files.getContent('182bd5e5-6e1a-4fe4-a799-aa6d9a6ab26e', { path: 'path' });

console.log(response);

const content = await response.blob();
console.log(content);

